mapper.select:

        SELECT
	     payment_id,
	     subject,
		 body,
		 notify_url,
	 	 type,
	 	 order_number,
   		 money,
   		 status,
   		 business_type,
   		 business_id,
   		 business_notify_url,
   		 acount_id,
   		 create_date,
   		 update_date
	FROM payment_tb


	AND type=#{type}


	AND business_id=#{businessId}


	AND acount_id=#{acountId}


	AND DATE(create_date)=DATE(#{createDate})


	AND DATE(update_date)=DATE(#{updateDate})


	AND status=#{status}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM payment_tb 


	AND type=#{type}


	AND business_id=#{businessId}


	AND acount_id=#{acountId}


	AND DATE(create_date)=DATE(#{createDate})


	AND DATE(update_date)=DATE(#{updateDate})


	AND status=#{status}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(payment_id)
FROM payment_tb


AND type>=#{type}


AND business_id=#{businessId}


AND acount_id=#{acountId}


AND DATE(create_date)=DATE(#{createDate})


AND DATE(update_date)=DATE(#{updateDate})


AND status=#{status}
